import React, { useState, useEffect } from 'react';
import './ImageSlider.css';
import PopupMenu from '../PopupMenu/PopupMenu.js';

const ImageSlider = ({ images, url }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('tab1'); // Default active tab
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  const menuItems = {
    tab1: [
      { name: "Samosa", price: "$3.00" },
      { name: "Chilli 65", price: "$5.50" },
      { name: "Gobi Machurian", price: "$3.00" },
      { name: "Hakka Noodles", price: "$5.50" },
      { name: "Manchow Soup", price: "$3.00" },
      { name: "Tomato Basil Soup", price: "$5.50" },
      { name: "Culets", price: "$3.00" },
      { name: "Spring Rolls", price: "$5.50" },
    ],
    tab2: [
      { name: "Butter Chicken", price: "$10.00" },
      { name: "Palak Paneer", price: "$9.00" },
      { name: "Panner Tikka", price: "$3.00" },
      { name: "Panner Bhurji", price: "$5.50" },
      { name: "Malai Kofta", price: "$3.00" },
    ],
    tab3: [
      { name: "Ras Malai", price: "$10.00" },
      { name: "Kulfi", price: "$9.00" },
      { name: "Rabdi", price: "$3.00" },
      { name: "Gulab Jamoon", price: "$5.50" },
      { name: "Kheer", price: "$3.00" },
      { name: "Halwa", price: "$5.50" },
    ],
    tab4: [
      { name: "Chai", price: "$10.00" },
      { name: "Lassi", price: "$9.00" },
      { name: "Badaam MIlk", price: "$3.00" },
      { name: "Taj", price: "$5.50" },
      { name: "Flying Horse", price: "$3.00" },
      { name: "Beera", price: "$5.50" },
      { name: "Soda", price: "$3.00" }
    ],
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentIndex(prevIndex => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
    }, 5000);

    return () => clearInterval(timer);
  }, [images.length]);

  const handlePopupOpen = () => {
    setIsPopupOpen(true);
  };

  const handlePopupClose = () => {
    setIsPopupOpen(false);
  };

  const selectTab = (tab) => {
    setActiveTab(tab);
  };

  const handleItemClick = (item) => {
    setSelectedItem(item);
    setSidebarOpen(true);
  };

  const closeSidebar = () => {
    setSidebarOpen(false);
  };

  return (
    <div className="image-slider">
      <div className="slider-container">
        {images.map((image, index) => (
          <div key={`slide-${index}`} className={`slide ${index === currentIndex ? 'active' : ''}`}>
            <img src={image} alt={`Slide ${index + 1}`} />
          </div>
        ))}
      </div>
      <div className="center-text">
        <h1 className='slider-title'>-Taste Of Home-</h1>
        <p className='slider-subtitle'>Authentic Flavour Of India</p>
      </div>
      <button className="center-button" onClick={handlePopupOpen}>
        View Menu
      </button>

      {isPopupOpen && (
        <PopupMenu
        menuItems={menuItems}
        activeTab={activeTab}
        selectTab={selectTab}
        handlePopupClose={handlePopupClose}
        handleItemClick={handleItemClick}
      />
      )}
      {sidebarOpen && (
        <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
          <h2 className='sidebar-title'>{selectedItem.name}</h2>
          {selectedItem && (
            <>
              <p>Desc: {selectedItem.name}</p>
              <p>Price: {selectedItem.price}</p>
            </>
          )}
          <button onClick={closeSidebar}>Close</button>
        </div>
      )}
    </div>
  );
};

export default ImageSlider;
