import React, { useState, useEffect } from 'react';
import './ImageSlider.css';

const ImageSlider = ({ images, url }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('tab1'); // Default active tab
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  const menuItems = {
    // ... your existing menuItems object ...
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentIndex(prevIndex => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
    }, 5000);

    return () => clearInterval(timer);
  }, [images.length]);

  const handlePopupOpen = () => {
    setIsPopupOpen(true);
  };

  const handlePopupClose = () => {
    setIsPopupOpen(false);
  };

  const selectTab = (tab) => {
    setActiveTab(tab);
  };

  const handleItemClick = (item) => {
    setSelectedItem(item);
    setSidebarOpen(true);
  };

  const closeSidebar = () => {
    setSidebarOpen(false);
  };

  return (
    <div className="image-slider">
      {/* Existing slider and button code */}
      <div className="slider-container">
        {/* ... */}
      </div>
      <div className="center-text">
        {/* ... */}
      </div>
      <button className="center-button" onClick={handlePopupOpen}>
        View Menu
      </button>

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-content">
            <div className="popup-tabs">
              {/* Tab buttons */}
            </div>
            <div className="popup-tab-content">
              {/* Existing code for tab content */}
              {Object.entries(menuItems).map(([tab, items]) => (
                activeTab === tab && (
                  <ul key={tab}>
                    {items.map((item, index) => (
                      <li key={index} onClick={() => handleItemClick(item)}>
                        {item.name} - {item.price}
                      </li>
                    ))}
                  </ul>
                )
              ))}
            </div>
            <button className="popup-close-button" onClick={handlePopupClose}>Close</button>
          </div>
        </div>
      )}

      {sidebarOpen && (
        <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
          <h2>Item Details</h2>
          {selectedItem && (
            <>
              <p>Name: {selectedItem.name}</p>
              <p>Price: {selectedItem.price}</p>
            </>
          )}
          <button onClick={closeSidebar}>Close</button>
        </div>
      )}
    </div>
  );
};

export default ImageSlider;
