import React, { useState, createContext } from 'react';

export const PopupContext = createContext();

export const PopupProvider = ({ children }) => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('tab1'); // Default active tab
  // Assuming you have items data
  const [items, setItems] = useState(/* Your items data here */);

  const handlePopupOpen = () => {
    setIsPopupOpen(true);
  };

  const handlePopupClose = () => {
    setIsPopupOpen(false);
  };

  const selectTab = (tab) => {
    setActiveTab(tab);
  };

  return (
    <PopupContext.Provider value={{ isPopupOpen, handlePopupOpen, handlePopupClose, activeTab, selectTab, items }}>
      {children}
    </PopupContext.Provider>
  );
};
